custom_scss:
.btn.btn-primary{
    background-color: #1071b6 !important
}
.page-card-head .app-logo{
    max-height: 135px !important;
}
.navbar-light{
    background: linear-gradient(to right, #00c5fb 0%, #0253cc 100%);
}
body.logged-out{
    background-size: cover;
    background-image: url("http://localhost:8000/files/image.png");
}
@media (max-width: 768px) {
  body {
    background-image:none;
    font-size: 18px;
    background-color: #f0f0f0;
  }
}

